generated.css:
h1 {
  margin: 0;
  font-size: 3em;
  font-weight: 900;
  line-height: 1.25em;
}

h1.huge {
  font-size: 7em;
}

h2 {
  margin: 0;
  font-weight: 500;
}

h5 {
  margin: 0;
  color: #aaa;
  font-size: 0.825em;
  font-weight: 900;
}

p,
li {
  color: #333;
  font-size: 0.875em;
  line-height: 1.75em;
}

.subheader {
  font-weight: 300;
  color: #aaa;
  font-size: 0.75em;
  line-height: 1.5em;
  margin-top: 12px;
}
.subheader a {
  font-size: 1em;
  text-transform: none;
  font-weight: 300;
}
.subheader a:after {
  bottom: 0px;
}

.subheader.huge {
  font-size: 1.75em;
}

a {
  text-transform: uppercase;
  text-decoration: none;
  font-weight: 900;
  font-size: 0.75em;
  color: #6EC6B1;
  line-height: 1.5em;
  transition: 0.2s;
  position: relative;
}
a:hover {
  transition: 0.2s;
  color: #49b89d;
}
a:hover:after {
  width: 100%;
  background: #49b89d;
}
a:after {
  position: absolute;
  left: 0;
  bottom: -2px;
  content: "";
  width: 0%;
  height: 2px;
  background: #6EC6B1;
  transition: 0.2s;
}

.underline {
  position: relative;
}

.mint {
  color: #6EC6B1;
}

.band-container {
  display: flex;
  flex-direction: column;
  padding: 0px;
  padding: 90px 120px;
  align-items: center;
}
.band-container p {
  color: #aaa;
}
.band-container .animal-plushies {
  display: flex;
  flex-direction: column;
  padding: 0px;
  flex-direction: row;
  text-align: center;
}
.band-container .animal-plushies > :not(:last-child) {
  margin-right: 12px;
}
.band-container .animal {
  width: 240px;
  display: flex;
  flex-direction: column;
  padding: 0px;
  align-items: center;
}
.band-container .animal > :not(:last-child) {
  margin-bottom: 24px;
}
.band-container img.small {
  width: 120px;
  margin-top: 24px;
  margin-bottom: 6px;
}
.band-container img.large {
  width: 150px;
  margin: 0;
}
.band-container .keys {
  display: flex;
  flex-direction: column;
  padding: 0px;
  flex-direction: row;
  flex-wrap: wrap;
  align-items: center;
  justify-content: center;
}
.band-container .key-press {
  text-transform: uppercase;
  text-decoration: none;
  font-weight: 900;
  font-size: 0.75em;
  color: #6EC6B1;
  line-height: 1.5em;
  background-color: transparent;
  border: none;
  outline: none;
  margin-bottom: 24px;
  cursor: pointer;
}
.band-container .key-press.active, .band-container .key-press:hover {
  color: #49b89d;
}
.band-container .key-press.active span, .band-container .key-press:hover span {
  border: 1px solid #49b89d;
  color: white;
  background-color: #6EC6B1;
}
.band-container .key-press span {
  margin-left: 4px;
  border: 1px solid #6EC6B1;
  padding: 4px 6px;
  border-radius: 2px;
}
